import { Awaitable, Preset } from '@unocss/core';
import { IconifyJSON } from '@iconify/types';

interface Options {
    scale?: number;
    mode?: 'mask' | 'background-img' | 'auto';
    prefix?: string;
    warn?: boolean;
    collections?: Record<string, IconifyJSON | undefined | (() => Awaitable<IconifyJSON | undefined>)>;
    extraProperties?: Record<string, string>;
    /**
     * Rule layer
     * @default 'icons'
     */
    layer?: string;
}
declare const preset: ({ scale, mode, prefix, warn, collections, extraProperties, layer, }?: Options) => Preset;

export { Options, preset as default, preset };
